<template>
  <div
    :style="{position: 'absolute',zIndex: zindex,bottom: '0px',left: '0px',right: '0px',background: '#fff'}"
    class="jfgrid-datavisual-quick-m" v-if="showList"
  >
    <el-button
      @click="$emit('closeChartShowList')"
      plain
      round
      size="small"
      style="width:100%;margin:2px 4px 8px 4px;"
      type="danger"
    >
      <i class="iconfont icon-guanbi" style="float:left;"></i> 关闭图表选择菜单
    </el-button>

    <el-radio-group size="mini" style="display:block;text-align:center;" v-model="currentPro">
      <el-radio-button :key="index" :label="item.type" v-for="(item,index) in config">{{item.name}}</el-radio-button>
    </el-radio-group>
    <div class="jfgrid-datavisual-quick-menu" id="jfgrid-datavisual-quick-menu">
      <div
        :data-type="item.type"
        :id="'jfgrid-datavisual-chart-menu-' + item.type"
        :key="index"
        @click="quickMenu"
        v-for="(item,index) in currentConfig.data"
      >
        <i :class="item.icon" aria-hidden="true" class="iconfont"></i>
        <span>{{item.name}}</span>
      </div>
    </div>
    <div
      @scroll="quickListScroll"
      class="jfgrid-datavisual-quick-list jfgrid-scrollbars"
      id="jfgrid-datavisual-quick-list"
    >
      <template v-for="(item,index) in currentConfig.data">
        <div :key="index" class="jfgrid-datavisual-quick-list-title">
          <a :data-type="item.type" :id="'jfgrid-datavisual-chart-listtitle-'+item.type">
            <i :class="item.icon" aria-hidden="true" class="iconfont"></i>
            {{item.name}}
          </a>
        </div>
        <div class="jfgrid-datavisual-quick-list-ul">
          <el-tooltip
            :content="chartItem.name"
            :key="index"
            :open-delay="500"
            effect="dark"
            placement="bottom"
            v-for="(chartItem,index) in item.data"
          >
            <div
              :chartAllType="chartItem.type"
              :class="chartItem.type==currentChartType? 'jfgrid-datavisual-quick-list-item-active':''"
              :data-style="chartItem.type.split('-')[2]"
              :data-type="chartItem.type.split('-')[1]"
              @click="changeChartType(chartItem.type)"
              class="jfgrid-datavisual-quick-list-item"
            >
              <img
                :src="chartItem.img.length==0?'data:image/svg+xml;base64,':chartItem.img"
                alt="图片"
              >
            </div>
          </el-tooltip>
        </div>
      </template>
    </div>
  </div>
</template>

<script>
import $ from "jquery";
import { mapState } from "vuex";
import { deepCopy } from "@/utils/util";
import { changeChangeAllType } from "@/utils/chartUtil";
import { chartreuse } from "color-name";
//图表选择组件
export default {
  name: "ChartList",
  props: {
        showList: {
      type: Boolean,
      default: false
    },
    zindex: {
      type: Number,
      default: 10,
    },
    currentChartType: {
      type: String,
      default: 'echarts|line|default'
    }
  },
  data: function() {
    return {
      config: [
        {
          type: "echarts",
          name: "百度echarts",
          data: [
            {
              type: "line",
              name: "折线图",
              icon: "icon-tubiaozhexiantu",
              data: [
                {
                  type: "echarts|line|default",
                  name: "折线图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|line|smooth",
                  name: "平滑折线图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|line|label",
                  name: "带标签的折线图",
                  img:
                    "data:image/png;base64,",
                },
              ],
            },
            {
              type: "area",
              name: "面积图",
              icon: "icon-fsux_tubiao_duijimianjitu",
              data: [
                {
                  type: "echarts|area|default",
                  name: "面积图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|area|stack",
                  name: "堆叠面积图",
                  img:
                    "data:image/png;base64,",
                },
              ],
            },
            {
              type: "column",
              name: "柱状图",
              icon: "icon-chart",
              data: [
                {
                  type: "echarts|column|default",
                  name: "柱状图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|column|stack",
                  name: "堆叠柱状图",
                  img:
                    "data:image/png;base64,",
                },
              ],
            },
            {
              type: "bar",
              name: "条形图",
              icon: "icon-fsux_tubiao_duijizhuzhuangtu1",
              data: [
                {
                  type: "echarts|bar|default",
                  name: "条形图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|bar|stack",
                  name: "堆叠条形图",
                  img:
                    "data:image/png;base64,",
                },
              ],
            },
            {
              type: "pie",
              name: "饼 图",
              icon: "icon-fsux_tubiao_nandingmeiguitu",
              data: [
                {
                  type: "echarts|pie|default",
                  name: "饼 图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|pie|split",
                  name: "分离型饼图",
                  img:
                    "data:image/png;base64,",
                },
                {
                  type: "echarts|pie|ring",
                  name: "环形饼图",
                  img:
                    "data:image/png;base64,",
                },
              ],
            },
          ],
        },
      ],
      currentPro: null,
      chartAllType: '',
      list_scroll_direction: -1000,
    };
  },
  computed: {
    ...mapState("chartSetting", ["chartLists", "currentChartIndex"]),

    chartPro() {
      return this.chartAllType.split("|")[0]
    },
    chartType() {
      return this.chartAllType.split("|")[1];
    },
    chartStyle() {
      return this.chartAllType.split("|")[2];
    },
    currentConfig: function() {
      for (var i = 0; i < this.config.length; i++) {
        if (this.config[i].type == this.chartPro) {
          return this.config[i];
        }
      }
      return this.config[0];
    },
  },
  watch: {
    currentChartType(val){
      this.chartAllType = val
    }
  },
  methods: {
    changeChartType: function(value) {
      // 相同类型或者currentChartIndex为null不执行
      if (this.currentChartIndex == null) {
        return null;
      }
      if (
        (this.chartLists[this.currentChartIndex].chartOptions,
        chartreuse == value)
      ) {
        return null;
      }
      changeChangeAllType(
        deepCopy(this.chartLists[this.currentChartIndex].chartOptions),
        value
      );

      this.currentChartType = value;
    },
    quickListScroll: function(e) {
      var scrollTop = $(e.currentTarget).scrollTop();
      var _this = this;
      $(e.currentTarget)
        .find("div.jfgrid-datavisual-quick-list-title")
        .each(function() {
          var $t = $(this),
            position = $t.position();
          //console.log($t.find("a").data("type"),position.top, scrollTop);
          if (scrollTop >= _this.list_scroll_direction) {
            if (position.top + 55 > 0) {
              $("#jfgrid-datavisual-quick-menu div").removeClass(
                "jfgrid-datavisual-quick-menu-active"
              );
              $(
                "#jfgrid-datavisual-chart-menu-" + $t.find("a").data("type")
              ).addClass("jfgrid-datavisual-quick-menu-active");
              return false;
            }
          } else {
            if (position.top - 55 >= 0) {
              $("#jfgrid-datavisual-quick-menu div").removeClass(
                "jfgrid-datavisual-quick-menu-active"
              );
              var $obj = $t.prev().prev();
              if ($obj.length == 0) {
                $obj = $t;
              }
              $(
                "#jfgrid-datavisual-chart-menu-" + $obj.find("a").data("type")
              ).addClass("jfgrid-datavisual-quick-menu-active");
              return false;
            }
          }
          setTimeout(function() {
            _this.list_scroll_direction = scrollTop;
          }, 0);
        });
    },
    quickMenu: function(e) {
      var $t = $(e.currentTarget);
      $("#jfgrid-datavisual-quick-menu div").removeClass(
        "jfgrid-datavisual-quick-menu-active"
      );
      $t.addClass("jfgrid-datavisual-quick-menu-active");
      var $scroll = $(
          "#jfgrid-datavisual-chart-listtitle-" + $t.data("type")
        ).parent(),
        p = $scroll.position().top;
      $("#jfgrid-datavisual-quick-list").scrollTop(
        p + 5 + $("#jfgrid-datavisual-quick-list").scrollTop()
      );
    },
  },
};
</script>

